sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    //Type appropriate comment here, and begin script below

	if(!g_form.isNewRecord()){
		g_form.setValue('u_what_needed',g_form.getValue('u_what_needed'));
		return;
	}

    g_form.clearOptions('u_what_needed');

    if (newValue === 'hr') {

        g_form.addOption('u_what_needed', 'hr3', 'Human Resource 1', 1);
        g_form.addOption('u_what_needed', 'hr2', 'Human Resource 2', 2);
        g_form.addOption('u_what_needed', 'other', 'Other', 3);
    } else if (newValue === 'facilities') {
        g_form.addOption('u_what_needed', 'facilities1', 'facilities 1', 1);
        g_form.addOption('u_what_needed', 'facilities2', 'facilities 2', 2);
        g_form.addOption('u_what_needed', 'other', 'Other', 3);
    } else if (newValue === 'legal') {
        g_form.addOption('u_what_needed', 'legal1', 'legal 1', 1);
        g_form.addOption('u_what_needed', 'legal2', 'legal 2', 2);
        g_form.addOption('u_what_needed', 'other', 'Other', 3);
    }



}
